package day13;

public class Ex06MathClass {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		double su1 = -3.1412315415;
		System.out.println("절대값 : " + Math.abs(su1));
		System.out.println("반올림 : " + Math.round(su1));
		System.out.println("내림 : " + Math.floor(su1));
		System.out.println("올림 : " + Math.ceil(su1));
	}

}
